function mousecursor() {
	if ($('body')) {
		const e = document.querySelector('.cursor-inner'),
			t = document.querySelector('.cursor-outer');
		let n,
			i = 0,
			o = !1;
		(window.onmousemove = function (s) {
			o || (t.style.transform = 'translate(' + s.clientX + 'px, ' + s.clientY + 'px)'), (e.style.transform = 'translate(' + s.clientX + 'px, ' + s.clientY + 'px)'), (n = s.clientY), (i = s.clientX);
		}),
			$('body').on('mouseenter', 'a, img, .cursor-pointer', function () {
				e.classList.add('cursor-hover'), t.classList.add('cursor-hover');
			}),
			$('body').on('mouseleave', 'a, img, .cursor-pointer', function () {
				($(this).is('a, img') && $(this).closest('.cursor-pointer').length) || (e.classList.remove('cursor-hover'), t.classList.remove('cursor-hover'));
			}),
			(e.style.visibility = 'visible'),
			(t.style.visibility = 'visible');
	}
}

$(function () {
	mousecursor();
});

function passwordCheck(password) {
	if (password.length >= 8)
	  strength += 1;
	if (password.match(/(?=.*[0-9])/))
	  strength += 1;
	if (password.match(/(?=.*[!,%,&,@,#,$,^,*,?,_,~,<,>,])/))
	  strength += 1;
	if (password.match(/(?=.*[a-z])/))
	  strength += 1;

	displayBar(strength);
  }

  function displayBar(strength) {
	$(".password-strength-group").attr('data-strength', strength);
  }

  $("#signupInputPassword").keyup(function () {
	strength = 0;
	var password = $(this).val();
	passwordCheck(password);
  });


