function lenisSetup() {
    const lenis = new Lenis({
        duration: 1.2,
		easing: (t) => (t === 1 ? 1 : 1 - Math.pow(2, -10 * t)),
		direction: "vertical",
		gestureDirection: "vertical",
		smoothWheel: true,
		smooth: true,
		smoothTouch: false,
		touchMultiplier: 2,
    })

	//lenis.scrollTo(document.querySelector('.contBody'))

    function raf(time) {
        lenis.raf(time)
        requestAnimationFrame(raf)
    }

    requestAnimationFrame(raf);
    
}

